import Image from "next/image";
import CheckCircleIcon from "@/assets/icons/check-circle.svg";
import ArrowUpRightIcon from "@/assets/icons/arrow-up-right.svg";
import grainImage from "@/assets/images/grain.jpg";

interface Project {
  company: string;
  year: string;
  title: string;
  results: { title: string }[];
  link: string;
  image: string;
}

export const ProjectCard = ({ project }: { project: Project }) => {
  return (
    <div
      className="bg-gray-800 overflow-hidden rounded-3xl relative z-0 
      border-2 border-white/20 pt-8 md:pt-12 px-8 md:px-10"
    >
      <div
        className="absolute inset-0 -z-10 opacity-5"
        style={{
          backgroundImage: `url(${grainImage.src})`,
        }}
      ></div>
      <div className="lg:grid grid-cols-2">
        <div>
          <div className="gradient-text font-bold gap-2 text-sm">
            <span>{project.company}</span>
            <br />
            <span>{project.year}</span>
          </div>

          <h3 className="text-2xl font-serif mt-2 md:mt-5 md:text-4xl">
            {project.title}
          </h3>
          <hr className="border-t-2 border-white/5 mt-4 md:mt-5" />
          <ul className="flex flex-col gap-4 mt-4 md:mt-5">
            {project.results.map((result) => (
              <li
                key={result.title}
                className="flex items-center gap-2 text-sm md:text-base text-white/50"
              >
                <CheckCircleIcon className="size-5 md:size-6" />
                <span>{result.title}</span>
              </li>
            ))}
          </ul>
          <a href={project.link} className="btn my-8">
            <span>Explore More</span>
            <ArrowUpRightIcon className="size-4" />
          </a>
        </div>
        <div>
          <Image
            src={project.image}
            alt={project.title}
            className="-mb-4 md:-mb-0"
          />
        </div>
      </div>
    </div>
  );
};
